import React, { useState } from "react";
import "./Carousel.css";
import Picture1 from "./Picture1";
import Picture2 from "./Picture2";
import Picture3 from "./Picture3";
import Picture4 from "./Picture4";
import BlockQuote1 from "./BlockQuote1";
import BlockQuote2 from "./BlockQuote2";
import BlockQuote3 from "./BlockQuote3";
import BlockQuote4 from "./BlockQuote4";
import Dot from "./Dot1selected";
import Dot2 from "./Dot2";
import Dot3 from "./Dot3";
import Dot4 from "./Dot4";

function Carousel() {
  const arrayEl = [
    <Picture1></Picture1>,
    <Picture2></Picture2>,
    <Picture3></Picture3>,
    <Picture4></Picture4>,
  ];
  const arrayEl2 = [
    <BlockQuote1></BlockQuote1>,
    <BlockQuote2></BlockQuote2>,
    <BlockQuote3></BlockQuote3>,
    <BlockQuote4></BlockQuote4>,
  ];

  let [currentPos, setCurrentPos] = useState(0);

  const rightBtnPlus = function () {
    if (currentPos < 3) {
      setCurrentPos(currentPos + 1);
    }
  };
  const leftBtnMinus = function () {
    if (currentPos > 0) {
      setCurrentPos(currentPos - 1);
    }
  };
  let arrayDots;
  if ((currentPos = 0)) {
    arrayDots = [<Dot></Dot>, <Dot2></Dot2>, <Dot3></Dot3>, <Dot4></Dot4>];
  }


  return (
    <div>
      <div className="carousel-container">
        {arrayEl[currentPos]}
        {arrayEl2[currentPos]}
        <button
          className="carousel-button carousel-button-left"
          aria-label="previous testimonal"
          onClick={leftBtnMinus}
        >
          <svg
            xmlns="http://www.w3.org/2000/svg"
            fill="none"
            viewBox="0 0 24 24"
            stroke-width="1.5"
            stroke="currentColor"
          >
            <path
              stroke-linecap="round"
              stroke-linejoin="round"
              d="M15.75 19.5L8.25 12l7.5-7.5"
            />
          </svg>
        </button>
        <button
          className="carousel-button carousel-button-right"
          aria-label="next testimonal"
          onClick={rightBtnPlus}
        >
          <svg
            xmlns="http://www.w3.org/2000/svg"
            fill="none"
            viewBox="0 0 24 24"
            stroke-width="1.5"
            stroke="currentColor"
          >
            <path
              stroke-linecap="round"
              stroke-linejoin="round"
              d="M8.25 4.5l7.5 7.5-7.5 7.5"
            />
          </svg>
        </button>
      </div>
      <div className="carousel-dots-container">{arrayDots}</div>
    </div>
  );
}

export default Carousel;
