import "./components/style-elements.css";
import Footer from "./components/Footer";
import ReadyButton from "./components/ReadyButton";
import CarouselDots from "./components/CarouselDots";
import Carousel from "./components/Carousel";
import HowToLists from "./components/HowToLists";
import ArrowDown from "./components/ArrowDown";
import PostHeroTitle from "./components/PostHeroTitle";
import ImagesCont from "./components/ImagesCont";
import HeroBelowSection from "./components/HeroBelowSection";
import Hero from "./components/Hero";
import Header from "./components/Header";

function App() {
  // ! HAVE THE CORRECT YEAR ON THE FOOTER
const yearEl = document.querySelector('.year');
const currentYear = new Date().getFullYear();
yearEl.textContent = currentYear;

// ! CLASS FOR THE OLD-SAFARI FLEX GAP ISSUE
function checkFlexGap() {
  var flex = document.createElement('div');
  flex.style.display = 'flex';
  flex.style.flexDirection = 'column';
  flex.style.rowGap = '1px';

  flex.appendChild(document.createElement('div'));
  flex.appendChild(document.createElement('div'));

  document.body.appendChild(flex);
  var isSupported = flex.scrollHeight === 1;
  flex.parentNode.removeChild(flex);

  if (!isSupported) document.body.classList.add('no-flexbox-gap');
}
checkFlexGap();
  return (
    <div>
      <Header></Header>
      <main className="index-main">
        <Hero></Hero>
        <HeroBelowSection></HeroBelowSection>
        <ImagesCont></ImagesCont>
        <PostHeroTitle></PostHeroTitle>
        <ArrowDown></ArrowDown>
        <HowToLists></HowToLists>
        <Carousel></Carousel>
        <CarouselDots></CarouselDots>
        <ReadyButton></ReadyButton>
        <Footer></Footer>
      </main>
    </div>
  );
}

export default App;
